mapper.update:
update reservation_tb
set status_enum = '취소'
where reservation_num = #{reservationNum}

mapper.select:
    	SELECT left(min(departure_time),4) as min_yaer, left(max(departure_time),4)as max_year 
    	FROM reservation_tb as r
		JOIN ticket_tb as t
		ON r.id = t.reservation_id
		WHERE user_id = #{userId}
		AND status_enum = #{statusEnum}
